import React from 'react';
import SQLTable from '../Shared/SQLTable';
import { Select, MenuItem } from '@material-ui/core';

export default class DoctorPanel extends React.Component {
    constructor(props) {
        super(props)
        this.tables = [
            {name: 'Karta pacjenta', headers: [], rows: [], clickedRows: []},
            {name: 'Grafik', headers: [], rows: [], clickedRows: []},
            {name: 'Zabiegi', headers: [], rows: [], clickedRows: []}
        ]
        this.selectedTable = 1
        this.ready = true
    }

    reload = () => {
        this.ready = false
        this.forceUpdate()
    }

    onRowClick = (rows) => {
        this.clickedRows = rows
    }

    changeTable = (event) => {
        this.selectedTable = this.tables.findIndex(table => table.name === event.target.value)
        this.forceUpdate()
    }

    render = () => {
        return(
            <div>
                {this.ready && (
                    <SQLTable headers={this.tables[this.selectedTable].headers} rows={this.tables[this.selectedTable].rows}>
                        <Select
                        value={this.tables[this.selectedTable].name}
                        onChange={this.changeTable}
                        >
                            {this.tables.map(table => <MenuItem value={table.name}>{table.name}</MenuItem>)}
                        </Select>
                    </SQLTable>
                )}
            </div>
        )
    }
}
