import React from 'react';
import { TextField } from '@material-ui/core';
import { FormDialog, QuestionDialog } from '../Shared/Dialog';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';

export class AddPatientDialog extends React.Component {
  static today = new Date()

  constructor(props) {
    super(props)
    this.state = {
      inputs: {
        pesel: '',
        name: '',
        surname: null,
        date_of_birth: null
      },
      errors: {
        pesel: false,
        name: false,
        surname: false,
        date_of_birth: false
      },
      errorsLabel: {
        pesel: '',
        name: '',
        surname: '',
        date_of_birth: ''
      },
      isCancelled: false,
    } 
  }

  validate = () => {
    const userCreated = this.props.users.some(row => row.nazwa === this.state.inputs.login)

    return { //true means error
      errors: {
        pesel: this.state.inputs.login.length !== 11 || userCreated,
        name: !this.state.inputs.password.length,
        surname: !this.state.inputs.nazwisko.length,
        date_of_birth: this.state.inputs.date_of_birth > AddPatientDialog.today
      },
      errorsLabel: {
        login: this.state.inputs.login.length !== 11 ? 'Niewłaściwy numer pesel' : (userCreated ? 'Taki uzytkownik juz istnieje' : ''),
        name: !this.state.inputs.imie.length ? 'Puste imie' : '',
        surname: !this.state.inputs.nazwisko.length ? 'Puste nazwisko': '',
        date_of_birth: this.state.inputs.date_of_birth > AddPatientDialog.today ? 'Niewłaściwa data urodzenia' : ''
      }
    }
  }

  setInputValue = (name, val) => {
    const state = this.state
    state.inputs[name] = val
    this.setState(state)
  }

  exit = () => {
    const state = this.state
    state.isCancelled = true
    this.setState(state)
  }

  setErrors = (errors, label) => {
    const state = this.state
    state.errors = errors
    state.errorsLabel = label
    this.setState(state)
  }

  submit = (button) => {
    if(button === 'cancel') {
      this.exit()
      this.props.onClose(button)
    } else {
      const errors = this.validate()
      const isError = Object.keys(errors.errors).some(key => errors.errors[key])

      if(isError) {
        this.setErrors(errors.errors, errors.errorsLabel)
      } else {
        this.props.setData(this.state.inputs)
        this.exit()
        this.props.onClose(button)
      }
    }
  }

  render = () => {
    return !this.state.isCancelled && (
    <FormDialog title={this.props.title} isOpen={true} message={this.props.message} onClose={(type) => {this.submit(type)}}>

      <TextField
      autoFocus
      required
      error={this.state.errors.name}
      helperText={this.state.errorsLabel.name}
      margin="dense"
      id="name"
      label="Imie"
      type="text"
      fullWidth
      onChange={(event) => {if(event.target.value !== null) this.setInputValue('name', event.target.value)}}
      inputProps={{ maxLength: 45 }}
      />

      <TextField
      autoFocus
      required
      error={this.state.errors.surname}
      helperText={this.state.errorsLabel.surname}
      margin="dense"
      id="surname"
      label="Nazwisko"
      type="text"
      fullWidth
      onChange={(event) => {if(event.target.value !== null) this.setInputValue('surname', event.target.value)}}
      inputProps={{ maxLength: 45 }}
      />

      <TextField
      autoFocus
      required
      error={this.state.errors.pesel}
      helperText={this.state.errorsLabel.pesel}
      margin="dense"
      id="pesel"
      label="Pesel"
      type="text"
      fullWidth
      onChange={(event) => {if(event.target.value !== null) this.setInputValue('pesel', event.target.value)}}
      inputProps={{ maxLength: 11 }}
      />

      <MuiPickersUtilsProvider utils={DateFnsUtils}>
        <KeyboardDatePicker
        margin="normal"
        id="date-picker-dialog"
        label="Data urodzenia"
        helperText={this.state.errors.date_of_birth}
        format="MM/dd/yyyy"
        value={AddPatientDialog.today}
        onChange={(date) => {if(date !== null) this.setInputValue('date_of_birth', date)}}
        KeyboardButtonProps={{
          'aria-label': 'change date',
        }}
        />
      </MuiPickersUtilsProvider>

    </FormDialog>
  )}
}

export class EditUserDialog extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      inputs: {
        password: null,
        type: this.props.selected.rola
      },
      isCancelled: false,
    } 
  }

  setInputValue = (name, val) => {
    const state = this.state
    state.inputs[name] = val
    this.setState(state)
  }

  exit = () => {
    const state = this.state
    state.isCancelled = true
    this.setState(state)
  }

  submit = (button) => {
    if(button === 'done') {
      this.props.setData(this.state.inputs)
    }

    this.exit()
    this.props.onClose(button)
  }

  render = () => {
    return !this.state.isCancelled && (
    <FormDialog title={this.props.title} isOpen={true} message={this.props.message} onClose={(type) => {this.submit(type)}}>

      <TextField
      autoFocus
      margin="dense"
      id="password"
      label="Haslo"
      type="password"
      fullWidth
      onChange={(event) => {this.setInputValue('password', event.target.value)}}
      inputProps={{ maxLength: 45 }}
      />

      <select name="Typ" onChange={(event) => {
        if(event.target.value === 'null') {
          this.setInputValue('type', null)
        } else {
          this.setInputValue('type', event.target.value)
        }}}
      >

        <option value="null" selected={this.props.selected.rola == null}>---</option>
        {this.props.types.map(type => <option value={type} selected={this.props.selected.rola === type}>{type}</option>)}

      </select>
    </FormDialog>
  )}
}

export const DeleteUserDialog = ({isOpen, title, onClose}) =>
    <QuestionDialog isOpen={isOpen} title={title} question="Czy jestes pewny?" answers={['cancel', 'delete']} onClose={onClose}/>
