            'addEmployee': { //ok
                open: false
            },
            'deleteEmployee': { //ok
                open: false
            },
            'addEmployeeSpecialization': {
                open: false
            },
            'addSpecialization': {
                open: false
            },
            'editSpecialization': {
                open: false
            },
            'deleteSpecialization': {
                open: false
            },
            'addTreatmentType': {
                open: false
            },
            'deleteTreatmentType': {
                open: false
            }
        }
        this.state['tables'] = [
                {
                    name: 'Etaty', 
                    headers: [], 
                    rows: [], 
                    get: this.retrieveData.bind(this, 'Etaty', 'show full time'),
                    selectable: true,
                    selectRowCheckbox: true
                },
                {
                    name: 'Pracownicy', 
                    headers: [], 
                    rows: [], 
