        const state = this.state
        state.clickedRows = []
        this.setState(state)
        this.ready = false  
    }

    changeTable = (name) => {
        const state = this.state
        state.selectedTable = this.state.tables.findIndex(table => table.name === name)
        this.setState(state)

        state.tables[state.selectedTable].get()
        this.reload()
    }

    closeDialog = (id, status) => {
        this.dialogs[id].open = false

        switch(id) {
            case 'addJobType':
                if(status === 'done') {
                    this.addJobType()
                }
            break

            case 'editJobType':
                if(status === 'done') {
                    this.editJobType()
                }
            break

            case 'deleteJobType':
                if(status === 'delete') {
                    this.deleteJobType()
                }
            break

            case 'addEmployee':
                if(status === 'done') {
                    this.addEmployee()
                }
            break

            case 'deleteEmployee':
                if(status === 'delete') {
                    this.deleteEmployee()
                }
            break

            case 'addEmployeeSpecialization':
                if(status === 'done') {
                    this.addEmployeeSpecialization()
                }
            break

            default:
        }

        this.reload()
    }

    set ready(value) {
        const state = this.state
        state.ready = value 
        this.setState(state) 
    }

    openDialog = (id) => {
        this.dialogs[id].open = true
        this.forceUpdate()
