import React from 'react';
import SQLTable from '../Shared/SQLTable';
import IconButton from "@material-ui/core/IconButton";
import AddOutlinedIcon from '@material-ui/icons/AddOutlined';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import RemoveCircleOutlineOutlinedIcon from '@material-ui/icons/RemoveCircleOutlineOutlined';
import {AddUserDialog, EditUserDialog, DeleteUserDialog} from './Dialogs'

export default class AdminPanel extends React.Component {
    constructor(props) {
        super(props)
        this.getTypes()
        this.getUsers()
        this.clickedRows = []
        this.types = []
        this.dialogs = {
            'addUser': {
                open: false
            },
            'editUser': {
                open: false
            },
            'deleteUser': {
                open: false
            }
        }
    }

    getUsers = () => {
        fetch(`http://${this.props.connection.ip}:${this.props.connection.port}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            operation: 'show users',
            data: this.props.user
          })
        })
        .then(res => res.json())
        .then(json => {
            this.headers = json.headers
            this.rows = json.rows
            this.forceUpdate()
        })
        .catch(error => {
            alert(error)
        })
    }

    getTypes = () => {
        fetch(`http://${this.props.connection.ip}:${this.props.connection.port}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            operation: 'show types',
            data: this.props.user
          })
        })
        .then(res => res.json())
        .then(json => {
            this.types = json
            this.forceUpdate()
        })
        .catch(error => {
            alert(error)
        })
    }

    deleteUsers = () => {
        let data = this.props.user
        data['users'] = this.clickedRows

        fetch(`http://${this.props.connection.ip}:${this.props.connection.port}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'text/plain',
              'Accept': 'application/json'
            },
            body: JSON.stringify({
              operation: 'delete users',
              data: data
            })
          })
          .then(res => res.json())
          .then(json => {
              if(json.error) {
                alert(json.error)
              } else {
                this.forceUpdate()
              }
          })
          .catch(error => {
              alert(error)
          })
    }

    openDialog = (id) => {
        this.dialogs[id].open = true
        this.forceUpdate()
    }

    closeDialog = (id, status) => {
        this.dialogs[id].open = false
        if(status === 'delete') {
            this.deleteUsers()
        }
        this.forceUpdate()
    }

    onRowClick = (rows) => {
        this.clickedRows = rows
    }

    render = () => {
        return(
            <div>
                {this.headers && this.rows && (
                    <SQLTable headers={this.headers} rows={this.rows} title="Users" onRowClick={this.onRowClick} selectRowChecbox='true'>
                        <IconButton onClick={this.openDialog.bind(this, 'addUser')}>
                            <AddOutlinedIcon/>
                        </IconButton>

                        <IconButton onClick={this.openDialog.bind(this, 'editUser')}>
                            <EditOutlinedIcon/>
                        </IconButton>

                        <IconButton onClick={() => {this.clickedRows.length ? this.openDialog('deleteUser') : alert('No selected user/s')}}>
                            <RemoveCircleOutlineOutlinedIcon/>
                        </IconButton>
                    </SQLTable>
                )}

                <AddUserDialog isOpen={this.dialogs.addUser.open} onClose={this.closeDialog.bind(this, 'addUser')} title="Add user" types={this.types}/>
                <EditUserDialog isOpen={this.dialogs.editUser.open} onClose={this.closeDialog.bind(this, 'editUser')} title="Edit user"/>
                <DeleteUserDialog isOpen={this.dialogs.deleteUser.open} onClose={this.closeDialog.bind(this, 'deleteUser')} 
                title={this.clickedRows.length>1 ? "Delete users" : "Delete user"}/>
            </div>
        )
    }
}
