import React from 'react'
import './Identification.css'

export default class Identification extends React.Component {

    testLogin = "abc";
    testPassword = "123";

    constructor() {
        super(null)
        this.state = {
            login: null,
            password: null
        }
    }

    newLogin(event) {
        this.setState({
            login: event.target.login,
            password: this.state.password
        })
    }

    newPassword(event) {
        this.setState({
            login: this.state.login,
            password: event.target.password
        })
    }

    login() {
        if(this.state.login == this.testLogin && this.state.password == this.testPassword) {
            alert('Logged in')
        } else {
            alert('Invalid login information')
        }
    }

    render() {
        return (
        <fieldset>
            <legend>Identification</legend>

            <label htmlFor="login">Login</label>
            <input type="text" id="login" value={this.state.login} onChange={this.newLogin}></input>

            <label htmlFor="password">Password</label>
            <input type="password" id="password" value={this.state.password} onChange={this.newPassword}></input>   

            <button type="submit" onClick={this.login}>Login</button> 
        </fieldset>);
    }
}
