import React from 'react';
import { TextField } from '@material-ui/core';
import { FormDialog, QuestionDialog } from '../Shared/Dialog';

export class AddUserDialog extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      inputs: {
        login: null,
        password: null,
        type: null
      },
      errors: {
        login: false,
        password: false
      },
      isCancelled: false
    } 
  }

  validate = () => {
    return {
      login: !!this.state.inputs.login,
      password: !!this.state.inputs.password
    }
  }

  setInputValue = (name, val) => {
    const state = this.state
    state.inputs[name] = val
    this.setState(state)
  }

  submit = (button) => {
    if(button === 'cancel') {
      this.setInputValue('isCancelled', true)
      this.props.onClose(button)
    } else {
      this.errors = this.validate()
      const isError = Object.keys(this.errors).some(key => this.errors[key])

      if(button === 'done' && !isError) {
        this.props.setData(this.getInput())
        this.setInputValue('isCancelled', true)
        this.props.onClose(button)
      }
    }
  }

  render = () => {
    return !this.state.isCancelled && (
    <FormDialog title={this.props.title} isOpen={true} onClose={(type) => {this.submit(type)}}>


      <TextField
      autoFocus
      required
      error={this.state.errors.login}
      margin="dense"
      id="login"
      label="Login"
      type="text"
      fullWidth
      onChange={(event) => {this.setInputValue('login', event.target.value)}}
      />

      <TextField
      autoFocus
      required
      error={this.state.errors.password}
      margin="dense"
      id="password"
      label="Password"
      type="password"
      fullWidth
      onChange={(event) => {this.setInputValue('password', event.target.value)}}
      />

      <select name="Type" onChange={(event) => {
        if(event.target.value === 'null') {
          this.setInputValue('type', null)
        } else {
          this.setInputValue('type', event.target.value)
        }}}
      >

        <option value="null" selected>Empty</option>
        {this.props.types.map(type => <option value={type}>{type}</option>)}

      </select>
    </FormDialog>
  )}
}

export const EditUserDialog = (props) =>
    <FormDialog isOpen={props.isOpen} onClose={props.onClose} title={props.title}>
      <TextField
            autoFocus
            required
            margin="dense"
            id="login"
            label="Login"
            type="text"
            fullWidth
          />

        <TextField
            autoFocus
            margin="dense"
            id="password"
            label="Password"
            type="password"
            fullWidth
          />

        <TextField
            autoFocus
            margin="dense"
            id="type"
            label="Type"
            type="password"
            fullWidth
          />
    </FormDialog>

export const DeleteUserDialog = ({isOpen, title, onClose}) =>
    <QuestionDialog isOpen={isOpen} title={title} question="Are you sure?" answers={['cancel', 'delete']} onClose={onClose}/>
