import React from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import SignIn from '../SignIn/SignIn';
import AdminPanel from '../AdminPanel/AdminPanel';
import DirectorPanel from '../DirectorPanel/DirectorPanel';
import DoctorPanel from '../DoctorPanel/DoctorPanel';
import ReceptionPanel from '../ReceptionPanel/ReceptionPanel';
import PrivateRoute from '../Shared/PrivateRoute';

class App extends React.Component {

  connection = {
    ip: '127.0.0.1',
    port:'1234'
  }

  constructor(props) {
    super(props)
    this.props = props
    this.state = {accountType: undefined}

    window.addEventListener('popstate', ev => {
      this.setState(ev.state)
    })
  }

  setAccountType = (type) => {
    this.setState({accountType: type})
    window.history.pushState(this.state, 'App', '/login')
  }

  getAccountType = () => {
    return this.state.accountType
  }

  render = () => {
    return (
      <BrowserRouter>
        <Switch>
          <Route exact path="/login">
            <SignIn connection={this.connection} onLogged={this.setAccountType}></SignIn>
          </Route>

          <PrivateRoute exact path="/account/admin" getAccountTypeUser={this.getAccountType} accountTypeComponent="admin">
            <AdminPanel connection={this.connection}></AdminPanel>
          </PrivateRoute>

          <Route exact path="/account/director">
            <DirectorPanel></DirectorPanel>
          </Route>

          <Route exact path="/account/reception">
            <ReceptionPanel></ReceptionPanel>
          </Route>

          <Route exact path="/account/doctor">
            <DoctorPanel></DoctorPanel>
          </Route>
        </Switch>

        <Redirect exact from="/" to="/login"/>
      </BrowserRouter>
    );
  }
}

export default App;
