import React from 'react';
import SQLTable from '../Shared/SQLTable';
import { Select, MenuItem } from '@material-ui/core';
import { Refresh } from '../Shared/Refresh';
import DbmsFetch from '../Shared/DbmsFetch';
import { Redirect } from 'react-router-dom';

export default class DoctorPanel extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tables: [
                {
                    name: 'Karta pacjenta', 
                    headers: [], 
                    rows: [], 
                    clickedRows: [], 
                    get: this.retrieveData.bind(this, 'Karta pacjenta', 'show patients cards')
                },
                {
                    name: 'Grafik', 
                    headers: [], 
                    rows: [], 
                    clickedRows: [], 
                    get: this.retrieveData.bind(this, 'Grafik', 'show schedule', 'pesel', this.pesel)
                },
                {
                    name: 'Zabiegi', 
                    headers: [], 
                    rows: [], 
                    clickedRows: [], 
                    get: this.retrieveData.bind(this, 'Zabiegi', 'show treatments')
                }
            ],
            selectedTable: 1,
            ready: false,
            redirect: false
        }

        this.fetch = new DbmsFetch(this.props.connection, this.props.user).fetch
        this.getPesel().then(() => {
            this.state.tables[this.state.selectedTable].get()
        })
    }

    getPesel = () => {
        return (
            this.fetch('show pesel')
            .then(json => {
                if(json.error) {
                    alert(json.error)
                } else {
                    if(json.pesel == null) {
                        alert('No configurated worker account. Please contant with director.')
                        const state = this.state
                        state.redirect = true
                        this.setState(state)
                    } else {
                        this.pesel = json.pesel
                    }
                }
            })
            .catch(error => {
                alert(error)
            })
        )
    }

    retrieveData = (tableName, operation, dataName, data) => {
        alert(`${operation} ${dataName} ${data}`)
        this.fetch(operation, dataName, data)
        .then(json => {
            if(json.error) {
                alert(json.error)
            } else {
                const state = this.state
                const indx = state.tables.findIndex((val, indx) => val.name === tableName)
                state.tables[indx].headers = json.headers
                state.tables[indx].rows = json.rows
                this.setState(state)
                this.reload()
            }
        })
        .catch(error => {
            alert(error)

        })
    }

    onRowClick = (rows) => {
        this.clickedRows = rows
    }

    reload = () => {
        this.ready = false  
    }

    changeTable = (event) => {
        const state = this.state
        state.selectedTable = this.state.tables.findIndex(table => table.name === event.target.value)
        this.setState(state)
        this.reload()

        state.tables[state.selectedTable].get()
    }

    set ready(value) {
        const state = this.state
        state.ready = value 
        this.setState(state) 
    }

    render = () => {
        return(
            <div> 
                <Refresh value={this.state.ready} onRefresh={() => {this.ready = true}}>
                    <SQLTable headers={this.state.tables[this.state.selectedTable].headers} rows={this.state.tables[this.state.selectedTable].rows}>
                        <Select value={this.state.tables[this.state.selectedTable].name} onChange={this.changeTable}>
                            {this.state.tables.map(table => <MenuItem value={table.name}>{table.name}</MenuItem>)}
                        </Select>
                        <label>{this.pesel}</label>
                    </SQLTable>
                </Refresh>

                {this.state.redirect && (<Redirect to='/login'/>)}
            </div>
        )
    }
}
