import React from 'react'
import Container from 'react-bootstrap/Container'
import './Identification.css'
import { Row } from 'react-bootstrap'

export default class Identification extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            login: null,
            password: null
        }

        this.newLogin = this.newLogin.bind(this)
        this.newPassword = this.newPassword.bind(this)
        this.login = this.login.bind(this)
    }

    newLogin(event) {
        var newState = this.state
        newState.login = event.target.value
        this.setState(newState)
    }

    newPassword(event) {
        var newState = this.state
        newState.password = event.target.value
        this.setState(newState)
    }

    login() {
        var request = new XMLHttpRequest()
        request.addEventListener('readystatechange', () => {
            if(request.status === 200 && request.readyState === 4) {
                const doc = JSON.parse(request.response)
                if(doc.logged === true) {
                    this.props.onLogin(this.state.login)
                }
            }
        })

        request.open('GET', `http://${this.props.hostname}:${this.props.port}`, true)
        request.send()
    }

    render() {
        return (
        <Container>
            <Row>
                <label className="col-md-2" htmlFor="login">Login</label>
                <input className="col-md-10" type="text" id="login" value={this.state.login} onChange={this.newLogin}></input>
            </Row>

            <Row>
                <label htmlFor="password">Password</label>
                <input type="password" id="password" value={this.state.password} onChange={this.newPassword}></input>   
            </Row>

            <Row>
                <button type="submit" onClick={this.login}>Login</button>
            </Row> 
        </Container>);
    }
}
