import React from 'react';
import SQLTable from '../Shared/SQLTable';
import IconButton from "@material-ui/core/IconButton";
import AddOutlinedIcon from '@material-ui/icons/AddOutlined';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import RemoveCircleOutlineOutlinedIcon from '@material-ui/icons/RemoveCircleOutlineOutlined';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button, TextField } from '@material-ui/core';

const AddUserDialog = (isOpen, onClose) => 
    <Dialog open={isOpen} onClose={onClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add user</DialogTitle>

        <DialogContent>
          <TextField
            autoFocus
            required
            margin="dense"
            id="name"
            label="Login"
            type="text"
            fullWidth
          />
        </DialogContent>

        <DialogActions>
          <Button onClick={onClose} color="primary">
            Cancel
          </Button>
          <Button onClick={onClose} color="primary">
            Done
          </Button>
        </DialogActions>
    </Dialog>

export default class AdminPanel extends React.Component {
    constructor(props) {
        super(props)
        this.getUsers()
    }

    getUsers = () => {
        fetch(`http://${this.props.connection.ip}:${this.props.connection.port}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            operation: 'show users',
            data: this.props.user
          })
        })
        .then(res => res.json())
        .then(json => {
            this.headers = json.headers
            this.rows = json.rows
            this.forceUpdate()
        })
        .catch(error => {
            alert(error)
        })
    }

    addUser = () => {

    }

    editUser = () => {

    }

    removeUser = () => {

    }

    render = () => {
        return(
            <div>
                {this.headers && this.rows && (
                    <SQLTable headers={this.headers} rows={this.rows} title="Users">
                        <IconButton onClick={this.addUser}>
                            <AddOutlinedIcon/>
                        </IconButton>

                        <IconButton onClick={this.editUser}>
                            <EditOutlinedIcon/>
                        </IconButton>

                        <IconButton onClick={this.removeUser}>
                            <RemoveCircleOutlineOutlinedIcon/>
                        </IconButton>
                    </SQLTable>
                )}

                <AddUserDialog isOpen={true} onClose={()=>alert('close')}/>
            </div>
        )
    }
}
