import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { DataGrid, ToolbarOptions } from 'tubular-react';
import { ColumnModel } from 'tubular-common';
import IconButton from "@material-ui/core/IconButton";
import AddOutlinedIcon from '@material-ui/icons/AddOutlined';
import { Button } from '@material-ui/core';

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function SQLTable(props) {
  const classes = useStyles();
  const [headers, setHeaders] = React.useState(props.headers)
  const [rows, setRows] = React.useState(props.rows)

  //const [selectedRows, setSelectedRows] = React.useState(new Set())

  let convHeaders = headers.map(({header, label}, colnum) => 
    new ColumnModel(header, {
      sortable: true,
      filterable: true,
      searchable: true
    }))


  const [columns, setColumns] = React.useState(convHeaders)

  const toolbarButton = new ToolbarOptions({
    customItems: <Button onClick={() => alert('HELLO')}>Force refresh</Button>,
  });

  return (
    <DataGrid
    columns={columns}
    dataSource={rows}
    gridName={props.title}
    onRowClick={(row) => alert(JSON.stringify(row))}
    toolbarOptions={toolbarButton}
   />
  )
}

