import React from 'react'
import './Identification.css'

export default class Identification extends React.Component {

    constructor(props) {
        super(props)

        this.host = props.hostname;
        this.port = props.port;
        this.onLogin = props.onLogin;

        this.state = {
            login: null,
            password: null
        }

        this.newLogin = this.newLogin.bind(this)
        this.newPassword = this.newPassword.bind(this)
        this.login = this.login.bind(this)
    }

    newLogin(event) {
        var state = this.state
        state.login = event.target.value
        this.setState(state)
    }

    newPassword(event) {
        var state = this.state
        state.password = event.target.value
        this.setState(state)
    }

    login() {
        var request = new XMLHttpRequest()
        request.addEventListener('readystatechange', () => {
            if(request.status === 200 && request.readyState === 4) {
                const doc = JSON.parse(request.response)
                if(doc.logged === true) {
                    this.onLogin(this.state.login)
                }
            }
        })

        request.open('GET', `http://${this.host}:${this.port}`, true)
        request.send()
    }

    render() {
        return (
        <fieldset>
            <legend>Identification</legend>

            <label htmlFor="login">Login</label>
            <input type="text" id="login" value={this.state.login} onChange={this.newLogin}></input>

            <label htmlFor="password">Password</label>
            <input type="password" id="password" value={this.state.password} onChange={this.newPassword}></input>   

            <button type="submit" onClick={this.login}>Login</button> 
        </fieldset>);
    }
}
