import React from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import SignIn from '../SignIn/SignIn';
import AdminPanel from '../AdminPanel/AdminPanel';
import DirectorPanel from '../DirectorPanel/DirectorPanel';
import DoctorPanel from '../DoctorPanel/DoctorPanel';
import ReceptionPanel from '../ReceptionPanel/ReceptionPanel';
import PrivateRoute from '../Shared/PrivateRoute';

class App extends React.Component {

  connection = {
    ip: '127.0.0.1',
    port:'1234'
  }

  constructor(props) {
    super(props)
    this.state = {
      user: {}
    }

    window.addEventListener('popstate', ev => {
      this.setState(ev.state)
    })
  }

  set user(user) {
    this.setState({user: user})
    window.history.pushState(this.state, 'App', '/login')
  }

  get user() {
    return this.state.user
  }

  render = () => {
    return (
      <BrowserRouter>
        <Switch>
          <Route exact path="/login">
            <SignIn connection={this.connection} onLogged={(usr) => {this.user = usr}}/>
          </Route>

          <PrivateRoute exact path="/account/admin" accountTypeUser={this.user['account-type']} accountTypeComponent="admin">
            <AdminPanel connection={this.connection} user={this.user}/>
          </PrivateRoute>

          <PrivateRoute exact path="/account/director" accountTypeUser={this.user['account-type']} accountTypeComponent="director">
            <DirectorPanel connection={this.connection} user={this.user}/>
          </PrivateRoute>

          <PrivateRoute exact path="/account/reception" accountTypeUser={this.user['account-type']} accountTypeComponent="reception">
            <ReceptionPanel connection={this.connection} user={this.user}/>
          </PrivateRoute>

          <PrivateRoute exact path="/account/doctor" accountTypeUser={this.user['account-type']} accountTypeComponent="doctor">
            <DoctorPanel connection={this.connection} user={this.user}/>
          </PrivateRoute>
        </Switch>

        <Redirect exact from="/" to="/login"/>
      </BrowserRouter>
    );
  }
}

export default App;
