import React from 'react';
import { DataGrid, ToolbarOptions } from 'tubular-react';
import { ColumnModel} from 'tubular-common';
import { Checkbox } from '@material-ui/core';
import { createRenderer } from 'react-dom/test-utils';

export default class SQLTable extends React.Component {

  constructor(props) {
    super(props)

    this.createToolbar()
    this.createColumns()
    this.createRows()

    this.setState({
      clickedRows: [],
      rows: this.rows
    })
  }

  createColumns = () => {
    this.columns = this.props.headers.map(({header, label}, colnum) => 
    new ColumnModel(header, {
      sortable: true,
      searchable: true
    }))
    this.columns.push(new ColumnModel('rownum', {
      visible: false,
      isKey: true
    }))
  }

  createRows = () => {
    if(this.props.selectRowChecbox) {
      this.columns.push(new ColumnModel('selected', {label: ' '}))
      this.rows = this.props.rows.map((val, indx) => {
          val['selected'] = <Checkbox></Checkbox>
          val['rownum'] = indx
          return val
      })
    } else {
      this.rows = this.props.rows.map((val, indx) => {
        val['rownum'] = indx
        return val
      })
    }
  }

  createToolbar = () => {
    this.toolbarButton = new ToolbarOptions({
      customItems: <div>{this.props.children}</div>
    });
  }

  onRowClick = (row) => {
    let clickedRowsCopy = Array.from(clickedRows)
    let indx = clickedRows.findIndex((val) => val.rownum === row.rownum)
    if(indx < 0) {  
      clickedRowsCopy.push(row)
      let rowsCopy = Array.from(rows)
      rowsCopy[row.rownum].selected = () => <Checkbox checked={true}></Checkbox>
      setRows(rowsCopy)

    } else {
      clickedRowsCopy.splice(indx, 1)
    }
    setClickedRows(clickedRowsCopy)
    props.onRowClick(clickedRowsCopy)
  } 

  renderer = () => {
    return (
      <DataGrid
      columns={this.columns}
      dataSource={this.state.rows}
      gridName={this.props.title}
      onRowClick={this.props.selectRowChecbox ? this.onRowClick : this.props.onRowClick}
      toolbarOptions={this.toolbarButton}
    />
  )}
}
