import React from 'react';
import SQLTable from '../Shared/SQLTable';
import { Select, MenuItem } from '@material-ui/core';

export default class DoctorPanel extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tables: [
                {name: 'Karta pacjenta', headers: [], rows: [], clickedRows: []},
                {name: 'Grafik', headers: [], rows: [], clickedRows: []},
                {name: 'Zabiegi', headers: [], rows: [], clickedRows: []}
            ],
            selectedTable: 1,
            ready: true
        }
    }

    reload = () => {
        this.ready = false
        this.forceUpdate()
    }

    onRowClick = (rows) => {
        this.clickedRows = rows
    }

    changeTable = (event) => {
        const state = this.state
        state.selectedTable = this.state.tables.findIndex(table => table.name === event.target.value)
        this.setState(state)
        this.reload()
    }

    render = () => {
        return(
            <div>
                {this.state.ready && (
                    <SQLTable headers={this.state.tables[this.state.selectedTable].headers} rows={this.state.tables[this.state.selectedTable].rows}>
                        <Select
                        value={this.state.tables[this.state.selectedTable].name}
                        onChange={this.changeTable}
                        >
                            {this.state.tables.map(table => <MenuItem value={table.name}>{table.name}</MenuItem>)}
                        </Select>
                    </SQLTable>
                )}
            </div>
        )
    }
}
