import React from 'react';
import SQLTable from '../Shared/SQLTable';
import IconButton from "@material-ui/core/IconButton";
import AddOutlinedIcon from '@material-ui/icons/AddOutlined';

export default class AdminPanel extends React.Component {
    constructor(props) {
        super(props)
        this.getUsers()
    }

    getUsers = () => {
        fetch(`http://${this.props.connection.ip}:${this.props.connection.port}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            operation: 'show users',
            data: this.props.user
          })
        })
        .then(res => res.json())
        .then(json => {
            this._headers = json.headers
            this._rows = json.rows
            this.forceUpdate()
        })
        .catch(error => {
            alert(error)
        })
    }

    get headers() {
        return this._headers
    }

    get rows() {
        return this._rows
    }

    render = () => {
        return(
            <div>
                {this.headers && this.rows && (
                    <SQLTable headers={this.headers} rows={this.rows} title="Users" children="<IconButton>
                            <AddOutlinedIcon/>
                        </IconButton>

                        <IconButton>
                            <AddOutlinedIcon/>
                        </IconButton>"/>
                )}
            </div>
        )
    }
}
