import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import Checkbox from '@material-ui/core/Checkbox';
import TableRow from '@material-ui/core/TableRow';

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function SQLTable(props) {
  const classes = useStyles();
  const [headers, setHeaders] = React.useState(props.headers)
  const [rows, setRows] = React.useState(props.rows)

  const [selectedRows, setSelectedRows] = React.useState(new Set())

  function onSelectRow(rownum) {
    if(selectedRows.has(rownum)) {
      selectedRows.delete(rownum)
    } else {
      selectedRows.add(rownum)
    }
    let state = new Set(selectedRows)
    setSelectedRows(state)
  }

  function onSelectAllRows() {
    if(selectedRows.size === rows.length) {
      selectedRows.clear()
    } else {
      rows.forEach((val, indx) => {
        selectedRows.add(indx)
      })
    }
    let state = new Set(selectedRows)
    setSelectedRows(state)
  }

  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {headers.map(({header, label}, colnum) => (
                <TableCell
                  key={headers}
                  align='center'
                >

                  {!colnum && 
                  (<Checkbox
                  indeterminate={selectedRows.size > 0 && selectedRows.size < rows.length}
                  checked={selectedRows.size === rows.length}
                  onChange={onSelectAllRows}
                  style={{float: "left"}}
                  inputProps={{ 'aria-label': 'select all desserts' }}
                  />)}

                  <span style={{'fontWeight': 'bold'}}>{label}</span>
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, rownum) => (
                <TableRow hover role="checkbox">
                  {headers.map(({header}, colnum) => (
                      <TableCell align='center'>

                        {!colnum && 
                        (<Checkbox
                        checked={selectedRows.has(rownum)}
                        onChange={onSelectRow.bind(this, rownum)}
                        style={{float: "left"}}
                        />)}

                        {row[header]}
                      </TableCell>
                    ))
                  }
                </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
