import React from 'react';
import { DataGrid, ToolbarOptions } from 'tubular-react';
import { ColumnModel} from 'tubular-common';
import { Checkbox } from '@material-ui/core';

export default function SQLTable(props) {
  const [headers, setHeaders] = React.useState(props.headers)
  const [clickedRows, setClickedRows] = React.useState([])

  //headers
  let convHeaders = headers.map(({header, label}, colnum) => 
    new ColumnModel(header, {
      sortable: true,
      searchable: true
    }))
  convHeaders.push(new ColumnModel('rownum', {
    visible: false,
    isKey: true
  }))


  //rows
  let newRows
  if(props.selectRowChecbox) {
    convHeaders.push(new ColumnModel('selected', {label: ' '}))
    newRows = props.rows.map((val, indx) => {
        val['selected'] = <Checkbox disabled></Checkbox>
        val['rownum'] = indx
        return val
    })
  } else {
    newRows = props.rows.map((val, indx) => {
      val['rownum'] = indx
      return val
    })
  }
  const [rows, setRows] = React.useState(newRows)


  //buttons
  const [columns, setColumns] = React.useState(convHeaders)
  const toolbarButton = new ToolbarOptions({
    customItems: <div>{props.children}</div>
  });



  function onRowClick(row) {
    let copy = Array.from(clickedRows)
    let indx = clickedRows.findIndex((val) => val === row)
    if(indx < 0) {  
      copy.push(row)
      const rownum = rows.findIndex(val => val.rownum === row.rownum)
      rows[rownum].selected = <Checkbox></Checkbox>
      setRows(rows)
    } else {
      copy.splice(indx, 1)
    }
    setClickedRows(copy)
    props.onRowClick(clickedRows)
  } 

  return (
    <DataGrid
    columns={columns}
    dataSource={rows}
    gridName={props.title}
    onRowClick={props.selectRowChecbox ? onRowClick : props.onRowClick}
    toolbarOptions={toolbarButton}
   />
  )
}
