import React from 'react';
import { TextField } from '@material-ui/core';
import { FormDialog, QuestionDialog } from '../Shared/Dialog';

export class AddUserDialog extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      inputs: {
        login: '',
        password: '',
        type: null
      },
      errors: {
        login: false,
        password: false
      },
      errorLabel: {
        login: '',
        password: ''
      },
      isCancelled: false,
    } 
  }

  validate = () => {
    const userCreated = this.props.users.some(name => name === this.state.inputs.login)

    return { //true means error
      error: {
        login: !this.state.inputs.login.length || userCreated,
        password: !this.state.inputs.password.length
      },
      errorLabel: {
        login: !this.state.inputs.login.length ? 'Empty login' : (userCreated ? 'User already created' : ''),
        password: !this.state.inputs.password.length ? 'Empty password' : ''
      }
    }
  }

  setInputValue = (name, val) => {
    const state = this.state
    state.inputs[name] = val
    this.setState(state)
  }

  exit = () => {
    const state = this.state
    state.isCancelled = true
    this.setState(state)
  }

  setErrors = (errors) => {
    const state = this.state
    state.errors = errors
    this.setState(state)
  }

  submit = (button) => {
    if(button === 'cancel') {
      this.exit()
      this.props.onClose(button)
    } else {
      const errors = this.validate()
      const isError = Object.keys(errors.error).some(key => errors.error[key])

      if(isError) {
        this.setErrors(errors)
      } else {
        this.props.setData(this.state.inputs)
        this.exit()
        this.props.onClose(button)
      }
    }
  }

  render = () => {
    return !this.state.isCancelled && (
    <FormDialog title={this.props.title} isOpen={true} onClose={(type) => {this.submit(type)}}>

      <TextField
      autoFocus
      required
      error={this.state.errors.login}
      helperText={this.state.errorsLabel.login}
      margin="dense"
      id="login"
      label="Login"
      type="text"
      fullWidth
      onChange={(event) => {if(event.target.value !== null) this.setInputValue('login', event.target.value)}}
      inputProps={{ maxLength: 45 }}
      />

      <TextField
      autoFocus
      required
      error={this.state.errors.password}
      helperText={this.state.errorsLabel.password}
      margin="dense"
      id="password"
      label="Password"
      type="password"
      fullWidth
      onChange={(event) => {if(event.target.value !== null) this.setInputValue('password', event.target.value)}}
      inputProps={{ maxLength: 45 }}
      />

      <select name="Type" onChange={(event) => {
        if(event.target.value === 'null') {
          this.setInputValue('type', null)
        } else {
          this.setInputValue('type', event.target.value)
        }}}
      >

        <option value="null" selected>Empty</option>
        {this.props.types.map(type => <option value={type}>{type}</option>)}

      </select>
    </FormDialog>
  )}
}

export const EditUserDialog = (props) =>
    <FormDialog isOpen={props.isOpen} onClose={props.onClose} title={props.title}>
      <TextField
            autoFocus
            required
            margin="dense"
            id="login"
            label="Login"
            type="text"
            fullWidth
          />

        <TextField
            autoFocus
            margin="dense"
            id="password"
            label="Password"
            type="password"
            fullWidth
          />

        <TextField
            autoFocus
            margin="dense"
            id="type"
            label="Type"
            type="password"
            fullWidth
          />
    </FormDialog>

export const DeleteUserDialog = ({isOpen, title, onClose}) =>
    <QuestionDialog isOpen={isOpen} title={title} question="Are you sure?" answers={['cancel', 'delete']} onClose={onClose}/>
